<?php

require_once(dirname(__FILE__).'/../../odtphp/library/odf.php');
require_once(dirname(__FILE__).'/../../htmlconverter/library/htmlConverter.php');

/**
 * Zim
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 * 
 * @package    openZIM
 * @subpackage model
 * @author     Christoph Herbst
 * @version    SVN: $Id: Builder.php 7490 2010-03-29 19:53:27Z jwage $
 */
class Zim extends BaseZim
{
	public function hasAnlagen()
	{
		return AnlageTable::getAllCount($this->getId()) > 0;
	}

	public function getStunden()
	{
		return StundeTable::getStundenSorted($this->getId());
	}

	public function delete(Doctrine_Connection $conn = null)
	{
		foreach( $this->getStunden() as $stunde )
		{
			$stunde->delete();
		}
  		return parent::delete($conn);
	}

        public function generateOdf()
	{
		$encode = false;
		$odf = new odf(dirname(__FILE__).'/../../odftmp/Zim_template.odt');
		$htmlConverter = new htmlConverter($odf->getStyleNames());
		$odf->setVars('nameZim',$this->getName(),true,'UTF-8');
		$odf->setVars('ptkuerzel',$this->getPtkuerzel(),true,'UTF-8');
		$odf->setVars('ptjahr',$this->getPtjahr(),true,'UTF-8');
		$odf->setStyleVars('ptkuerzel',$this->getPtkuerzel(),true,'UTF-8');
		$odf->setStyleVars('ptjahr', $this->getPtjahr(),true,'UTF-8');
		$odf->setVars('zieleZim',$htmlConverter->getODF($this->getZiele()),$encode,'UTF-8');
		$odf->setVars('zielGruppe',$this->getZielGruppe(), true,'UTF-8');
		$odf->setVars('roterFaden',$htmlConverter->getODF($this->getRoterFaden()),$encode,'UTF-8');
		$odf->setMetaVars('title', $this, true,'UTF-8');
		$stdn = $odf->setSegment('stdn');
		foreach($this->getStunden() AS $stunde) {
			$stdn->setVars('nameStunde',$stunde->getName(),true,'UTF-8');
			$stdn->merge();
		}
		$odf->mergeSegment($stdn);
		$koffer = $odf->setSegment('koffer');
		foreach($this->getStunden() AS $stunde) {
			foreach($stunde->getAnlagen() AS $anlage) {
			    $koffer->kuerzel($anlage->getKuerzel());
			    $koffer->lnr($anlage->getLnrStr());
			    $koffer->setVars('kofferInhalt',$htmlConverter->getODF($anlage->getKofferInfo()),$encode,'UTF-8');
			    $koffer->merge();
			}
		}
		$odf->mergeSegment($koffer);
		$stunden = $odf->setSegment('stunden');
		foreach($this->getStunden() AS $stunde) {
			$stunden->setVars('lnrStunde',$stunde->getLnr());
			$stunden->setVars('nameStunde',$stunde->getName(),true,'UTF-8');
			if ( $stunde->getAnlagen()->count() == 0 )
				$stunden->anlagen->delete();
			foreach($stunde->getAnlagen() AS $anlage) {
			    $stunden->anlagen->name($anlage->getName());
			    $stunden->anlagen->zeit($anlage->getZeit());
			    $stunden->anlagen->setVars('ziel',
				$htmlConverter->getODF($anlage->getZiel()), $encode,'UTF-8');
			    $stunden->anlagen->setVars('kurzinhalt',
				$htmlConverter->getODF($anlage->getKurzinhalt()), $encode,'UTF-8');
			    $stunden->anlagen->setVars('methode',
				$htmlConverter->getODF($anlage->getMethode()), $encode,'UTF-8');
			    $stunden->anlagen->setVars('rolletm',
				$htmlConverter->getODF($anlage->getRolleTm()), $encode,'UTF-8');
			    $stunden->anlagen->name2($anlage->getName());
			    $stunden->anlagen->setVars('material',
				$htmlConverter->getODF($anlage->getMaterial()), $encode,'UTF-8');
			    $stunden->anlagen->merge();
			}
			$stunden->merge();
		}
		$odf->mergeSegment($stunden);
		return $odf;
        }

        public function getFilename()
        {
        	return 'PT_'.$this->getPtkuerzel().'_'.$this->getPtjahr().'_ZIM.odt';
        }
	public function __toString()
	{
		return 'PT '.$this->getPtKuerzel().' '.$this->getPtJahr().' â€” '.$this->getName();
	}
}
